        [WebMethod]
        public DataCabAddInfo GetDataCabInfo(int start, int size, int g3e_fid)
        {
            DataCabAddInfo osp = new DataCabAddInfo();

            using (Entities ctxData = new Entities())
            {
                int count = (from c in ctxData.WV_CAB_ADDINFO
                             select c).Count();
                osp.max = count;

                var query = (from d in ctxData.WV_CAB_ADDINFO
                             //join e in ctxData.OSP_FIB_FACILITIES on d.G3E_FID equals e.G3E_FID
                             where d.G3E_FID == g3e_fid
                             orderby d.PWR_CAB_ID
                             select new
                             {
                                 d.G3E_FID,
                                 d.PWR_CAB_ID,
                                 d.PWR_CAB_CONDITION,
                                 d.PRO_ELCB_BRAND,
                                 d.RC_BRAND,
                                 d.BATT_BRAND,
                                 d.AIRCOND_IN_OUT,
                                 d.DATA_NO
                             }).Skip(start).Take(size);

                foreach (var a in query)
                {
                    CabAddInfo CabAddInfo = new CabAddInfo();
                    CabAddInfo.G3E_FID = a.G3E_FID.ToString();
                    CabAddInfo.PWR_CAB_ID = a.PWR_CAB_ID;
                    CabAddInfo.PWR_CAB_CONDITION = a.PWR_CAB_CONDITION;
                    CabAddInfo.PRO_ELCB_BRAND = a.PRO_ELCB_BRAND;
                    CabAddInfo.RC_BRAND = a.RC_BRAND;
                    CabAddInfo.BATT_BRAND = a.BATT_BRAND;
                    CabAddInfo.DATA_NO = a.DATA_NO.ToString();

                    osp.DataCabAddInfoList.Add(CabAddInfo);
                    System.Diagnostics.Debug.WriteLine(CabAddInfo.PWR_CAB_ID);

                }
            }

            return osp;
        }

