                    var queryChkPCShare = (from c in ctxDataShare.WV_CAB_ADDINFO
                                           where c.PWR_CAB_ID == m.PWR_CAB_ID
                                           select new { c.G3E_FID });

                    foreach (var a in queryChkPCShare)
                    {
                        string sqlNewStr1 = "update WV_CAB_ADDINFO set PWR_CAB_ID = '" + m.PWR_CAB_ID + "', PWR_CAB_CONDITION = '" + m.PWR_CAB_CONDITION + "', EQUIP_CAB_CONDITION = '" + m.EQUIP_CAB_CONDITION + "',";
