                        sqlNewStr1 += "PRO_ELCB_CAPASITY = '" + m.PRO_ELCB_CAPASITY + "', PRO_ELCB_DATE = TO_DATE('" + m.PRO_ELCB_DATE.Date.ToString("MM/dd/yyyy") + "','MM/dd/yyyy'),PRO_MCB_BRAND = '" + m.PRO_MCB_BRAND + "',PRO_MCB_CAPASITY = '" + m.PRO_MCB_CAPASITY + "',";
