                                bq.PU_ID,
                                bq.PU_DESC,
                                bq.PU_UOM,
                                bq.NET_PRICE,
                                bq.ISP_OSP,
                                bq.ITEM_NO,
                                bq.CONTRACT_DESC,
                                bq.CONTRACT_NO
                            };


                int row_count = (from bq in ctxData.WV_BOQ_DATA
